import './loginStyles.css';

const Login = (props) => {

    const handleLogin = () => {
        clearErrors();
        fire
          .auth()
          .signInWithEmailAndPassword(email, password)
          .catch((err) => {
            switch (err.code) {
              case "auth/invalid-email":
              case "auth/user-disabled":
              case "auth/user-not-found":
                setEmailError(err.message);
                break;
              case "auth/wrong-password":
                setPasswordError(err.message);
                break;
            }
          });
      };

      const handleSignUp = () => {
        clearErrors();
        fire
          .auth()
          .createUserWithEmailAndPassword(email, password)
          .catch((err) => {
            switch (err.code) {
              case "auth/invalid-already-in-use":
              case "auth/invalid-email":
                setEmailError(err.message);
                break;
              case "auth/weak-password":
                setPasswordError(err.message);
                break;
            }
          });
      };

      const handleLogout = () => {
        fire.auth().signOut();
      };

      const authListener = () => {
        fire.auth().onAuthStateChanged((user) => {
          if (user) {
            clearInputs();
            setUser(user);
          } else {
            setUser("");
          }
        });
      };

      const clearInputs = () => {
        setEmail("");
        setPassword("");
      };

      const clearErrors = () => {
        setEmailError("");
        setPasswordError("");
      };

      useEffect(() => {
        authListener();
      }, []);
  return (
  <div className="login-form">
  <section className="login">
      <div className="loginContainer">
          <label>Username</label><br/>
          <input type="email" autoFocus required value={email} onChange={(e) => setEmail(e.target.value)}></input>
          <p>{emailError}</p>
          <label>Password</label><br/>
          <input type="password" autoFocus required value={password} onChange={(e) => setPassword(e.target.value)}></input>
          <p>{passwordError}</p>
          <div className="btnContainer">
              {hasAccount ? (
                  <>
                  <button onClick={handleLogin}>Sign in</button>
                  <p>Don't have an account ? <span onClick={() => setHasAccount(!hasAccount)}>Sign up</span></p>
                  </>
              ) : (
                <>
                <button onClick={handleSignUp}>Sign up</button>
                <p>Have an account ? <span onClick={()=> setHasAccount(!hasAccount)}>Sign in</span></p>
                </>
              )}
          </div>
      </div>
  </section>
  </div>

  )};

export default Login;
