import "./mainLogin.css";
import { Link } from "react-router-dom";
import { Button, Form } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import Header from "../commons/Header/Header";
import Footer from "../commons/Footer/Footer";

const MainLoginPage = (props) => {

  const {
    email,
    setEmail,
    password,
    setPassword,
    handleLogin,
    handleSignUp,
    hasAccount,
    setHasAccount,
    emailError,
    passwordError,
  } = props;

  return (
    <div className="login-form">
      <Header />
      {/* <Form
        // onSubmit={submitHandler}
        style={{
          width: "20rem",
          margin: "0 auto",
          marginTop: "100px",
          padding: "40px 25px 40px 25px",
          backgroundColor: "#222",
        }}
      >
        <Form.Group controlId="formBasicEmail">
          <Form.Label>Email address:</Form.Label>
          <Form.Control
            type="email"
            placeholder="email@email.com"
            name="email"
            value={email}
            onSubmit={(e) => setEmail(e.target.value)}
          />
          <Form.Text className="text-muted">
            We'll never share your email with anyone else.
          </Form.Text>
        </Form.Group>

        <Form.Group controlId="formBasicPassword">
          <Form.Label>Password:</Form.Label>
          <Form.Control
            type="password"
            placeholder="Password"
            name="password"
            value={password}
            onSubmit={(e) => setPassword(e.target.value)}
          />
        </Form.Group>
        <Button variant="primary" type="submit" onClick={handleLogin}>
          Login
        </Button>
        <Link className="regClass" to="/login-register/MainRegisterPage">
          / Register
        </Link>
      </Form> */}
      <section className="login">
        <div className="loginContainer">
          <label>Username</label><br/>
          <input
            type="email"
            autoFocus
            required
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
           <p className="errorMsg">{emailError}</p>
          <label>Password</label><br/>
          <input
            type="password"
            required
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <p className="errorMsg">{passwordError}</p>
          <div className="btnContainer">
            {hasAccount ? (
              <>
              <button onClick={handleLogin}>Sign in</button>
              <p>Don't have an account ? <span onClick>Sign up</span></p>
              </>
            ) : (
              <>
              <button onClick={handleSignUp}>Sign up</button>
              <p>Have an account ? <span>Sign in</span></p>
              </>
            )}
          </div>
        </div>
      </section>

      <Footer />
    </div>
  );
};

export default MainLoginPage;
